var log = function () {
	
	var fs = require('fs');  

	/*	
	 *	写入日志
	 */
	this.write = function (path, msg) {
		fs.appendFile(path, msg, function (error) {
			if (error == false) {
				console.log('log is error!');
				return false;
			}
			return true;
		});
	};
	
}

module.exports = log;